mapper.select:
SELECT max(step) 
FROM cm_board 
WHERE pgroup = #{cm_group}

mapper.update:
	 UPDATE cm_board
     SET cm_title = #{cm_title}
        ,cm_content = #{cm_content}
     WHERE cm_no = #{cm_no}
     AND cm_writer = #{cm_writer}

	 UPDATE cm_board
	 SET status = 'N'
 	 WHERE cm_no = #{cm_no}
 	 AND cm_writer = #{cm_writer}

mapper.select:
SELECT count(*)
FROM cm_board 
WHERE status = 'Y'
AND cm_group = #{cm_no}
AND depth != 1
ORDER BY cm_group, step, depth;
