mapper.insert:


{CALL DECLARE BEGIN
	UPDATE qna_board SET step = step + 1
	WHERE root = #{root } AND step > #{step };

	INSERT INTO qna_board (num, root, title, content, writer, step, indent)
	VALUES (qna_board_seq.NEXTVAL, #{root }, #{title }, #{content }, #{writer }, #{step } + 1, #{indent } + 1);
END}
